package com.chainsys.novbatch.string;

public class OccurenceString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	String input="Hello, World";
	
	System.out.println("The first position is no of 'o' letter:"+input.indexOf('o'));
	System.out.println("The first position is no of ',' letter:"+input.indexOf(','));
	System.out.println("The last position is no of ',' letter:"+input.lastIndexOf(','));

	System.out.println("The last position is no of 'o' letter:"+input.lastIndexOf('o'));

	


		
	}

}
